// public/sw.js - Service Worker for PWA
/* eslint-disable no-restricted-globals */
import { precacheAndRoute } from 'workbox-precaching';
import { registerRoute } from 'workbox-routing';
import { CacheFirst, NetworkFirst, StaleWhileRevalidate } from 'workbox-strategies';
import { ExpirationPlugin } from 'workbox-expiration';
import { CacheableResponsePlugin } from 'workbox-cacheable-response';

// Precache all assets generated by the build process
precacheAndRoute(self.__WB_MANIFEST);

// Activate immediately and take control
self.addEventListener('install', event => {
  self.skipWaiting();
});

self.addEventListener('activate', event => {
  event.waitUntil(self.clients.claim());
});

// Cache images with CacheFirst strategy
registerRoute(
  ({ request }) => request.destination === 'image',
  new CacheFirst({
    cacheName: 'images-cache',
    plugins: [
      new ExpirationPlugin({
        maxEntries: 100,
        maxAgeSeconds: 30 * 24 * 60 * 60, // 30 days
      }),
      new CacheableResponsePlugin({
        statuses: [0, 200],
      }),
    ],
  })
);

// Cache TMDB API responses
registerRoute(
  ({ url }) => url.origin === 'https://api.themoviedb.org',
  new NetworkFirst({
    cacheName: 'tmdb-api-cache',
    plugins: [
      new ExpirationPlugin({
        maxEntries: 50,
        maxAgeSeconds: 24 * 60 * 60, // 24 hours
      }),
      new CacheableResponsePlugin({
        statuses: [0, 200],
      }),
    ],
  })
);

// Cache CSS, JS with StaleWhileRevalidate
registerRoute(
  ({ request }) => request.destination === 'style' || request.destination === 'script',
  new StaleWhileRevalidate({
    cacheName: 'static-resources',
  })
);

// Cache Google Fonts
registerRoute(
  ({ url }) =>
    url.origin === 'https://fonts.googleapis.com' || url.origin === 'https://fonts.gstatic.com',
  new CacheFirst({
    cacheName: 'google-fonts',
    plugins: [
      new ExpirationPlugin({
        maxEntries: 30,
        maxAgeSeconds: 365 * 24 * 60 * 60, // 1 year
      }),
    ],
  })
);

// Handle offline page
self.addEventListener('fetch', event => {
  // Navigation requests: try network first, fallback to cache/offline page
  if (event.request.mode === 'navigate') {
    event.respondWith(
      (async () => {
        try {
          const preloadResponse = await event.preloadResponse;
          if (preloadResponse) {
            return preloadResponse;
          }

          const networkResponse = await fetch(event.request);
          return networkResponse;
        } catch (err) {
          const cache = await caches.open('static-resources');
          const cached = await cache.match('/offline.html');
          return cached || new Response('Offline', { status: 503, statusText: 'Offline' });
        }
      })()
    );
    return;
  }
});

// ============================================
// DOWNLOAD MANAGER SUPPORT
// ============================================

const DOWNLOAD_CACHE = 'nikzflix-downloads-v1';

// Listen for download requests from the app
self.addEventListener('message', event => {
  if (event.data && event.data.type === 'DOWNLOAD_VIDEO') {
    const { url, id, metadata } = event.data;
    handleDownload(url, id, metadata, event.ports[0]);
  } else if (event.data && event.data.type === 'DELETE_DOWNLOAD') {
    const { id } = event.data;
    handleDeleteDownload(id);
  }
});

// Handle video download with progress tracking
async function handleDownload(url, id, metadata, port) {
  try {
    const response = await fetch(url);

    if (!response.ok) {
      port.postMessage({ type: 'ERROR', error: 'Failed to fetch video' });
      return;
    }

    const reader = response.body.getReader();
    const contentLength = +response.headers.get('Content-Length');

    let receivedLength = 0;
    let chunks = [];

    while (true) {
      const { done, value } = await reader.read();

      if (done) break;

      chunks.push(value);
      receivedLength += value.length;

      // Send progress update
      if (contentLength) {
        const progress = (receivedLength / contentLength) * 100;
        port.postMessage({ type: 'PROGRESS', progress });
      }
    }

    // Combine chunks into single blob
    const blob = new Blob(chunks);

    // Cache the downloaded video
    const cache = await caches.open(DOWNLOAD_CACHE);
    const cacheResponse = new Response(blob, {
      headers: {
        'Content-Type': response.headers.get('Content-Type'),
        'Content-Length': receivedLength,
        'X-Download-Metadata': JSON.stringify(metadata),
      },
    });

    await cache.put(`/download/${id}`, cacheResponse);

    // Notify completion
    port.postMessage({ type: 'COMPLETE', size: receivedLength });
  } catch (error) {
    console.error('Download error:', error);
    port.postMessage({ type: 'ERROR', error: error.message });
  }
}

// Handle download deletion
async function handleDeleteDownload(id) {
  try {
    const cache = await caches.open(DOWNLOAD_CACHE);
    await cache.delete(`/download/${id}`);
  } catch (error) {
    console.error('Delete error:', error);
  }
}

// Serve downloaded videos
registerRoute(
  ({ url }) => url.pathname.startsWith('/download/'),
  async ({ url }) => {
    const cache = await caches.open(DOWNLOAD_CACHE);
    const response = await cache.match(url.pathname);

    if (response) {
      return response;
    }

    return new Response('Download not found', { status: 404 });
  }
);

// ============================================
// END DOWNLOAD MANAGER SUPPORT
// ============================================

console.log('ðŸš€ Service Worker Loaded - NikzFlix TV PWA Ready!');
